import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import "./updateUser.css";

export default function UpdateUser() {
  const location = useLocation();
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [email, setEmail] = useState(""); // Initialize email with the current value from location.

  useEffect(() => {
    // Set the initial values from the location state.
    setFname(location.state.data.fname);
    setLname(location.state.data.lname);
    setEmail(lo)
  }, [location]);

  const updateData = () => {
    // Make sure you send the updated fname and lname values from the form.
    fetchUserData({ fname, lname });
  };

  const fetchUserData = ({ fname, lname }) => {
    fetch("http://localhost:5000/updateUser", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        id: location.state.data._id,
        fname,
        lname,
      }),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error("Network response was not ok");
        }
        return res.json();
      })
      .then((data) => {
        console.log(data);
        window.localStorage.clear();
        window.location.href = "/userHome";
      })
      .catch((error) => {
        console.error("Error updating user data: ", error);
      });
  };

  return (
    <div>
      <form className="update_form">
        <h2 className="header">Update Account</h2>
        <div className="larger_inputContainer">
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="text"
              placeholder="First Name"
              name="fname"
              value={fname}
              onChange={(e) => setFname(e.target.value)}
              required
            />
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Last Name"
              name="lname"
              value={lname}
              onChange={(e) => setLname(e.target.value)}
              required
            />
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Email"
              name="email"
              value={email}
              disabled
            />
            <button onClick={updateData} className="btn btn-primary button">
              Submit
            </button>
          </div>
        </div>
      </form>
    </div>
  );
}
