import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import "./updateUser.css";

export default function UpdateUser() {
  const location = useLocation();
  const [fname, setFname] = useState(location.state.fname);
  const [lname, setLname] = useState(location.state.lname);
  const [email] = useState(location.state.email); // email is disabled, no need for state

  // Add state for updated values if needed

  return (
    <div>
      <form className="update_form">
        <h2 className="header">Update Account</h2>
        <div className="larger_inputContainer">
          <div className="mb-3">
            First Name
            <input
              className="form-control larger_input"
              type="text"
              placeholder="First Name"
              name="fname"
              value={fname}
              required
              // Add an onChange handler if you want to capture changes
            />
            Last Name
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Last Name"
              name="lname" // Corrected name attribute
              value={lname}
              required
              // Add an onChange handler if you want to capture changes
            />
            Email
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Email"
              name="email" // Corrected name attribute
              value={email}
              disabled
            />
            <button type="submit" className="btn btn-primary button">
              Submit
            </button>
          </div>
        </div>
      </form>
    </div>
  );
}
