import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';

export default function UpdateCompany({ visible, closeModal }) {
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    setIsModalOpen(visible);
  }, [visible]);

  return (
    <Modal
      isOpen={isModalOpen}
      onRequestClose={closeModal} // Ensure closeModal is a function
      style={{
        overlay: {
          backgroundColor: "rgba(0, 0, 0, 0.5)",
        },
        content: {
          width: "600px",
          height: "600px",
          transition: "right 0.3s ease",
          right: isModalOpen ? 0 : "-600px",
        },
      }}
    >
       <div className="update_form">
    <h2 className="header">Update Company Data</h2>
   <div className="larger_inputContainer">
        <input
          className="larger_input"
          type="text"
          placeholder="First Name"
          name="fname"
          value={fname}
          onChange={(e) => setFname(e.target.value)}
          required
        />
        <input
          className="larger_input"
          type="text"
          placeholder="Last Name"
          name="lname"
          value={lname}
          onChange={(e) => setLname(e.target.value)}
          required
        />
