import React, { useState,useEffect } from "react";
import Modal from "react-modal";
import "./signup.css";
import { Link } from "react-router-dom";

export default function SignupComponent2({ visible, closeModal }) {
  const [formData, setFormData] = useState({
    fname: "",
    lname: "",
    email: "",
    password: "",
    confirmPassword: "",
    userType: "user", // Default to "user"
    companyName: "", // New field for company name
  });

  const [formErrors, setFormErrors] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    setIsModalOpen(visible);
  }, [visible]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.email) {
      errors.email = "Email is required";
    }

    if (!formData.password) {
      errors.password = "Password is required";
    }

    if (!formData.confirmPassword) {
      errors.confirmPassword = "Confirm Password is required";
    } else if (formData.password !== formData.confirmPassword) {
      errors.confirmPassword = "Passwords do not match";
    }

    if (formData.userType === "company" && !formData.companyName) {
      errors.companyName = "Company Name is required";
    }

    setFormErrors(errors);

    return Object.keys(errors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      fetch("http://localhost:5000/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify({
          fname: formData.fname,
          lname: formData.lname,
          email: formData.email,
          password: formData.password,
          userType: formData.userType,
          companyName: formData.companyName,
        }),
      })
        .then((res) => {
          if (!res.ok) {
            throw new Error("Network response was not ok");
          }
          return res.json();
        })
        .then((data) => {
          setSuccessMessage("Account Creation Successful");
          setErrorMessage("");
          setFormData({
            fname: "",
            lname: "",
            email: "",
            password: "",
            confirmPassword: "",
            userType: "user",
            companyName: "",
          });

          setTimeout(() => {
            setSuccessMessage("");
            closeModal();
          }, 2000);
        })
        .catch((error) => {
          setSuccessMessage("");
          setErrorMessage("Account Creation failure");
          console.error(error);

          if (
            error instanceof Error &&
            error.message.includes(
              "E11000 duplicate key error collection: dbcproject.User index: email_1"
            )
          ) {
            setErrorMessage("Email is already in use. Please use a different email.");
          }
        });
    }
  };

  return (
    <Modal
      isOpen={isModalOpen}
      onRequestClose={closeModal}
      style={{
        overlay: {
          backgroundColor: "rgba(0, 0, 0, 0.5)",
        },
        content: {
          width: "600px",
          height: "4500px",
          margin: '0px'
        },
      }}
    >
      <form onSubmit={handleSubmit}>
        {successMessage && (
          <p className="success-message" style={{ color: "green" }}>
            {successMessage}
          </p>
        )}
        {errorMessage && (
          <p className="error-message" style={{ color: "red" }}>
            {errorMessage}
          </p>
        )}
        <div>
          <div>
            Register AS
            <br />
            <label>
              <input
                type="radio"
                name="userType"
                value="user"
                checked={formData.userType === "user"}
                onChange={handleChange}
              />
              User
            </label>
            <br />
            <label>
              <input
                type="radio"
                name="userType"
                value="company"
                checked={formData.userType === "company"}
                onChange={handleChange}
              />
              Company
            </label>
          </div>

          {formData.userType === "company" ? (
            <div className="mb-3">
              <input
                className="form-control larger_input"
                type="text"
                placeholder="Company Name"
                name="companyName"
                value={formData.companyName}
                onChange={handleChange}
                required
              />
              {formErrors.companyName && (
                <p className="error-message">{formErrors.companyName}</p>
              )}
            </div>
          ) : null}

          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="text"
              placeholder="First Name"
              name="fname"
              value={formData.fname}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Last Name"
              name="lname"
              value={formData.lname}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="email"
              placeholder="Email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            {formErrors.email && (
              <p className="error-message">{formErrors.email}</p>
            )}
          </div>
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="password"
              placeholder="Password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            {formErrors.password && (
              <p className="error-message">{formErrors.password}</p>
            )}
          </div>
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="password"
              placeholder="Confirm Password"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
            />
            {formErrors.confirmPassword && (
              <p className="error-message">{formErrors.confirmPassword}</p>
            )}
          </div>
        </div>

        <br />
        <button className="btn btn-success" type="submit">
          Sign Up
        </button>
      </form>
    </Modal>
  );
}
