import React, { useState, useEffect } from "react";
import { useNavigate} from "react-router-dom";
import { PieChart, Pie, Tooltip } from "recharts";
import "./superHome.css";
export default function SuperUserData() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const [ShowSignupForm, setShowSignupForm] = useState(false);
  const [data01, setData01] = useState([]);

  const navigate = useNavigate();

  const fetchData = () => {
    const superUserAuthenticated = localStorage.getItem(
      "superUserAuthenticated"
    );

    if (!superUserAuthenticated) {
      navigate("/superUser/login");
      return;
    }

    fetch("http://localhost:5000/getAllData")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        setLoading(false);
        setData01([
          { name: "Users", value: data.data.users.length },
          { name: "Companies", value: data.data.companies.length },
        ]);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  };

  useEffect(() => {
    fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const createSuperUser = () => {
    // Implement the logic for creating a new company here
    // You can show a form or send a request to the server
  };

  const deleteUser = (id, name) => {
    const confirmDelete = window.confirm(
      `Are you sure you want to delete ${name}`
    );
    if (confirmDelete) {
      fetch(`http://localhost:5000/deleteUser/${id}`, {
        method: "DELETE",
      })
        .then((response) => {
          if (response.status === 200) {
            fetchData();
          } else {
            console.error("Error deleting user:", response.statusText);
          }
        })
        .catch((error) => {
          console.error("Error deleting user:", error);
        });
    }
  };

  const deleteCompany = (id, name) => {
    const confirmDelete = window.confirm(
      `Are you sure you want to delete ${name}`
    );
    if (confirmDelete) {
      fetch(`http://localhost:5000/deleteCompany/${id}`, {
        method: "DELETE",
      })
        .then((response) => {
          if (response.status === 200) {
            fetchData();
          } else {
            console.error("Error deleting company:", response.statusText);
          }
        })
        .catch((error) => {
          console.error("Error deleting company:", error);
        });
    }
  };

  const toggleLoginForm = () => {
    setShowSignupForm(!ShowSignupForm);
  };

  const logout = () => {
    window.localStorage.clear();
    navigate("/superUser/login");
  };

  return (
    <div>
      <div className="button-container">
        <button className="btn btn-info" onClick={toggleLoginForm}>
          Create SuperUser
        </button>
      </div>

      {/* Display the mini signup if showSignupForm is true */}
      {ShowSignupForm && (
        <div className="modal-container">
          <div className="modal-form">
            {/* Your mini Signup  form content */}
            <input type="email" placeholder="Email" required /> 
            <br />
            <input type="password" placeholder="Confirm Password" required />
            <br />
            <input type="password" placeholder="Password" />
            <button onClick={createSuperUser}>Create</button>
            <button onClick={toggleLoginForm}>Close</button>
          </div>
        </div>
      )}

      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <table className="table table-striped table-bordered">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Email</th>
                <th>User Type</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {data.data.users.map((user) => (
                <tr key={user._id}>
                  <td>{user._id}</td>
                  <td>
                    {user.fname} {user.lname}
                  </td>
                  <td>{user.email}</td>
                  <td>{user.userType}</td>
                  <td
                    className="btn btn-danger"
                    onClick={() =>
                      deleteUser(user._id, `${user.fname} ${user.lname}`)
                    }
                  >
                    Delete
                  </td>
                </tr>
              ))}
              {data.data.companies.map((company) => (
                <tr key={company._id}>
                  <td>{company._id}</td>
                  <td>
                    {company.fname} {company.lname}
                  </td>
                  <td>{company.email}</td>
                  <td>{company.userType}</td>
                  <td
                    className="btn btn-danger"
                    onClick={() =>
                      deleteCompany(company._id, `${company.email}`)
                    }
                  >
                    Delete
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <p>TOTAL COMPANIES: {data.data.companies.length}</p>
          <p>TOTAL USERS: {data.data.users.length}</p>
          <p>
            TOTAL ACCOUNTS:{" "}
            {parseInt(data.data.users.length) +
              parseInt(data.data.companies.length)}
          </p>
          <button className="btn btn-primary" onClick={logout}>
            Logout
          </button>

          <PieChart width={1000} height={400}>
            <Pie
              data={data01}
              dataKey="value"
              nameKey="name"
              cx="50%"
              cy="50%"
              outerRadius={130}
              fill="#8884d8"
            ></Pie>
            <Tooltip />
          </PieChart>
        </div>
      )}
    </div>
  );
}
