import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./updateUser.css";

export default function UpdateUser() {
  const location = useLocation();
  const navigate = useNavigate();
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [email, setEmail] = useState("");
  const [updateSuccess, setUpdateSuccess] = useState(false); // State for displaying update success message

  useEffect(() => {
    if (location.state) {
      setFname(location.state.data.fname);
      setLname(location.state.data.lname);
      setEmail(location.state.data.email);
    }
  }, [location]);

  const updateData = () => {
    fetch("http://localhost:5000/updateUser", {
      method: "P",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        id: location.state.data._id,
        fname: fname,
        lname: lname,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.status) {
          setUpdateSuccess(true); // Set update success state to true
          navigate("/userHome");
        }
      });
  };

  return (
    <div>
      <form className="update_form">
        <h2 className="header">Update Account</h2>
        <div className="larger_inputContainer">
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="text"
              placeholder="First Name"
              name="fname"
              value={fname}
              onChange={(e) => setFname(e.target.value)}
              required
            />
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Last Name"
              name="lname"
              value={lname}
              onChange={(e) => setLname(e.target.value)}
              required
            />
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Email"
              name="email"
              value={email}
              disabled
            />
            <button onClick={updateData} className="btn btn-primary button">
              Submit
            </button>
          </div>
        </div>
      </form>
      {updateSuccess && (
        <div className="alert alert-success" role="alert">
          Update successful! Your credentials have been updated.
        </div>
      )}
    </div>
  );
}
