import React, { useState } from 'react';
import Modal from "react-modal"
export default function CreateSuperUser({ visible, closeModal }) {
  const [formData, setFormData] = useState({
        fname: '',
        lname: '',
        email: '',
        password: '',
        confirmPassword: '',
      });

      const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
          ...formData,
          [name]: value,
        });
      };

      const handleSubmit = (e) => {
        e.preventDefault();

        if (formData.password !== formData.confirmPassword) {
          alert('Password and confirm password do not match');
          return;
        }

        // Prepare the data to send to the server
        const dataToSend = {
          fname: formData.fname,
          lname: formData.lname,
          email: formData.email,
          password: formData.password,
          userType: 'superuser',
        };

        // Make an API call to create the super user
        fetch('http://localhost:5000/createSuperUser', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(dataToSend),
        })
          .then((response) => response.json())
          .then((data) => {
            // Handle the API response, e.g., show a success message or redirect to another page
            console.log(data);
          })
          .catch((error) => {
            // Handle any errors, e.g., display an error message
            console.error(error);
          });
      };
  return (
    <Modal
    isOpen={visible}
    onRequestClose={closeModal}
    style={{
      overlay: {
        backgroundColor: "black",
      },
      content: {
        width: "600px",
        height: "600px",
      },
    }}
  >
//       <form onSubmit={handleSubmit}>
//         <div>
//           <label>First Name</label>
         <input
            type="text"
            name="fname"
            value={formData.fname}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Last Name</label>
          <input
            type="text"
            name="lname"
            value={formData.lname}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Confirm Password</label>
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Create Super User</button>
      </form>

  </Modal>
);
}
