import React, { useEffect, useState } from "react";
import "./userDetails.css";
import UserHome from "../userHome";
import CompanyHome from "../companyHome";

export default function UserDetails() {
  const [userData, setUserData] = useState(null); // State to store user data
  const [isCompany, setIsCompany] = useState(false);

  useEffect(() => {
    fetch("http://localhost:5000/userData", {
      method: "POST",
      crossDomain: true,
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        "Access-Control-Allow-Origin": "*",
      },
      body: JSON.stringify({
        token: window.localStorage.getItem("token"),
        userType:user
      }),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error("Network response was not ok");
        }
        return res.json();
      })
      .then((data) => {
        setUserData(data);
        if (data.status === "error" && data.data === "token expired") {
          alert("Token expired. Please log in again.");
          window.localStorage.clear();
          window.location.href = "/login";
        } else if (data.data && data.data.userType === "Company") {
          setIsCompany(true);
        }
      })
      .catch((error) => {

      });
  }, [ ]);

  const logOut = () => {
    window.localStorage.clear();
    window.location.href = "/login";
    alert(`see you soon ${userData.data.fname}`);
  };

  return (
    <div className="details">
      {isCompany ? (
        <h1>
       <CompanyHome/>
        </h1>
      ) : null}

      {userData && userData.data ? <UserHome userData={userData.data} /> : null}

      <button onClick={logOut} className="logout_button btn btn-primary">
        Logout
      </button>
    </div>
  );
}
