import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function UserHome() {
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = window.localStorage.getItem("token");

    if (!token) {
      // Handle the case where there is no token in local storage (user is not logged in)
      navigate("/login"); // You can redirect to the login page or handle it as needed
      return;
    }

    fetch("http://localhost:5000/userData", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        token: JSON.parse(token), // Parse the token as JSON
      }),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error("Network response was not ok");
        }
        return res.json();
      })
      .then((data) => {
        if (data.status === "error" && data.data === "token expired") {
          alert("Token expired. Please log in again.");
          window.localStorage.clear();
          navigate("/login"); // Redirect to the login page
        } else {
          setUserData(data);
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }, [navigate]);

  return (
    <div>
      {userData ? (
        <div>
          <i
            className="fa-solid fa-user-pen"
            onClick={() => navigate("/updateUser", { state: userData })}
          ></i>
          {userData.data.fname} {userData.data.lname}
          <h4>
            {userData.data.email} ({userData.data.userType})
          </h4>
        </div>
      ) : (
        <p>Loading user data...</p>
      )}
    </div>
  );
}
