import React, { useState } from "react";
import Modal from "react-modal";
import "./createSuperUser.css"; // Import a CSS file for your custom styles

export default function CreateSuperUser({ visible, closeModal }) {
  const [formData, setFormData] = useState({
    fname: "",
    lname: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      alert("Password and confirm password do not match");
      return;
    }

    // Prepare the data to send to the server
    const dataToSend = {
      fname: formData.fname,
      lname: formData.lname,
      email: formData.email,
      password: formData.password,
      userType: "superuser",
    };

    // Make an API call to create the super user
    fetch("http://localhost:5000/createSuperUser", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(dataToSend),
    })
      .then((response) => response.json())
      .then((data) => {
        // Handle the API response, e.g., show a success message or redirect to another page
        console.log(data);
      })
      .catch((error) => {
        // Handle any errors, e.g., display an error message
        console.error(error);
      });
  };

  return (
    <Modal
      isOpen={visible}
      onRequestClose={closeModal}
      className="custom-modal" // Apply custom styles via a CSS class
    >
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            name="fname"
            value={formData.fname}
            placeholder="First Name"
            onChange={handleChange}
          />
        </div>
        <div>
          <input
            type="text"
            name="lname"
            value={formData.lname}
            placeholder="Last Name"
            onChange={handleChange}
          />
        </div>
        <div>
          <input
            type="email"
            name="email"
            value={formData.email}
            placeholder="Email"
            onChange={handleChange}
          />
        </div>
        <div>
          <input
            type="password"
            name="password"
            value={formData.password}
            placeholder="Password"
            onChange={handleChange}
          />
        </div>
        <div>
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            placeholder="Confirm password"
            onChange={handleChange}
          />
        </div>
        <br />
        <button className="btn btn-success" type="submit">Create Super User</button>
      </form>
    </Modal>
  );
}
