import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import "./updateUser.css";

export default function UpdateUser() {
  const location = useLocation();
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [email, setEmail] = useState("");

  useEffect(() => {
    // Fetch user data when the component mounts
    fetchUserData();
  }, []);

  const fetchUserData = () => {
    // Pass the user's ID to the server
    const userId = location.state.data._id;
    fetch("http://localhost:5000/updateUser", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        id: userId,
      }),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error("Network response was not ok");
        }
        return res.json();
      })
      .then((data) => {
        // Check for token expiration
        if (data.data === "token expired") {
          alert("Token expired. Please log in again.");
          window.localStorage.clear();
          window.location.href = "/login";
        } else {
          // Update state with fetched user data
          setFname(data.data.fname);
          setLname(data.data.lname);
          setEmail(data.data.email);
        }
      })
      .catch((error) => {
        console.error("Error fetching user data: ", error);
      });
  };

  const updateData = () => {
    console.log("First Name:", fname);
    console.log("Last Name:", lname);
  };

  return (
    <div>
      <form className="update_form">
        <h2 className="header">Update Account</h2>
        <div className="larger_inputContainer">
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="text"
              placeholder="First Name"
              name="fname"
              value={fname}
              onChange={(e) => setFname(e.target.value)}
              required
            />
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Last Name"
              name="lname"
              value={lname}
              onChange={(e) => setLname(e.target.value)}
              required
            />
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Email"
              name="email"
              value={email}
              disabled
            />
            <button onClick={updateData} className="btn btn-primary button">
              Submit
            </button>
          </div>
        </div>
      </form>
    </div>
  );
}
