import React, { useEffect, useState } from "react";
import "./userDetails.css";
import UserHome from "../userHome";
import CompanyHome from "../companyHome";

export default function UserDetails() {
  const [userData, setUserData] = useState(null);
  const [isCompany, setIsCompany] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:5000/userData?token=${window.localStorage.getItem("token")}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      }
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error("Network response was not ok");
        }
        return res.json();
      })
      .then((data) => {
        setUserData(data);
        if (data.status === "error" && data.data === "token expired") {
          alert("Token expired. Please log in again.");
          window.localStorage.clear();
          window.location.href = "/login";
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }, []);

  const logOut = () => {
    window.localStorage.clear();
    window.location.href = "/login";
    alert(`See you soon ${userData.data.fname}`);
  };

  return (
    <div className="details">
      {isCompany && <CompanyHome userData={userData} />}
      {userData && userData.data && !isCompany && (
        <>
          <UserHome userData={userData.data} />
          <button onClick={logOut} className="logout_button btn btn-primary">
            Logout
          </button>
        </>
      )}
    </div>
  );
}
