import React, { useState, useEffect } from "react";
import Modal from "react-modal";

export default function Login2({ visible, closeModal, register }) {
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    setIsModalOpen(visible);
  }, [visible]);

  return (
    <Modal
      isOpen={isModalOpen}
      onRequestClose={closeModal}
      style={{
        overlay: {
          backgroundColor: "rgba(0, 0, 0, 0.5)",
        },
        content: {
          width: "600px",
          height: "500px",
          margin: "0px",
        },
      }}
    >
      <form className="login_form" onSubmit={handleSubmit}>
        <h1 className="heading">Login</h1>
        <div className="large_inputContainer">
          <div className="mb-3">
            <input
              type="email"
              className="form-control large_input"
              placeholder="Email"
              id="exampleInputEmail1"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="mb-3">
            <input
              type="password"
              className="form-control large_input"
              placeholder="Password"
              id="exampleInputPassword1"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="mb-3">
            <label>Login as: </label>
            <select
              name="userType"
              value={formData.userType}
              onChange={handleInputChange}
            >
              <option value="user">User</option>
              <option value="company">Company</option>
            </select>
          </div>
        </div>

        {error && <div className="error-message">{error}</div>}

        <button
          type="submit"
          className="btn btn-primary large_button"
          disabled={loading}
        >
          {loading ? "Logging in..." : "Submit"}
        </button>
        <br />
        <div className="no_account">
          <i className="btn btn-link" onClick={openModal}>
            Don't have an account?Signup
          </i>

          <SignupComponent visible={ModalVisible} closeModal={closeModal} />
        </div>
      </form>
    </Modal>
  );
}
