import { initializeApp } from "firebase/app";
import { GoogleAuthProvider, getAuth, signInWithPopup,signOut } from 'firebase/auth'

const firebaseConfig = {
  apiKey: "AIzaSyBfgfUmItmJIDn0Q7IsVcRD_udh45M0H_Y",
  authDomain: "finalproject-3db27.firebaseapp.com",
  projectId: "finalproject-3db27",
  storageBucket: "finalproject-3db27.appspot.com",
  messagingSenderId: "400588295804",
  appId: "1:400588295804:web:65f8ce45bbf6505ad283f9",
  measurementId: "G-Y4QY4Q3496"
};

const app = initializeApp(firebaseConfig);
export const auth = getAuth(app)

const provider = new GoogleAuthProvider()

export const signInwithGoogle = () => {
  signInWithPopup(auth, provider)
    .then((result) => {
      const name = result.user.displayName;
      const email = result.user.email;
      const profilePic = result.user.photoURL

      localStorage.setItem('name', name);
      localStorage.setItem('email', email)
      localStorage.setItem('photo', profilePic)
    })
    .catch((error) => {
      console.log(error);
    })
}
