import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./updateUser.css";

export default function UpdateUser() {
  const location = useLocation();
  const navigate = useNavigate();
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [email, setEmail] = useState("");
  const [updateSuccess, setUpdateSuccess] = useState(false);

  useEffect(() => {
    if (location.state && location.state.data) {
      const { fname, lname, email } = location.state.data;
      setFname(fname);
      setLname(lname);
      setEmail(email);
    }
  }, [location]);
  console.log(location.state.userData.fname);
  const updateData = () => {
    fetch("http://localhost:5000/updateUser", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        id: location.state.userData._id,
        fname: location.state.userData.fname,
        lname: location.state.userData.lname,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data)
        if (data.status) {
          setUpdateSuccess(true);
          navigate("/userHome");
        }
      });
  };

  return (
    <div>
      <form className="update_form">
        <h2 className="header">Update Account</h2>
        <div className="larger_inputContainer">
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="text"
              placeholder="First Name"
              name="fname"
              value={fname}
              onChange={(e) => setFname(e.target.value)}
              defaultValue={location.state.userData.fname}
              required
            />
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Last Name"
              name="lname"
              value={lname}
              onChange={(e) => setLname(e.target.value)}
              defaultValue={location.state.userData.lname}
              required
            />
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Email"
              name="email"
              value={location.state.userData.email}

              disabled
            />

            <button onClick={updateData} className="btn btn-primary button">
              Submit
            </button>
          </div>
        </div>
      </form>
      {updateSuccess && (
        <div className="alert alert-success" role="alert">
          Update successful! Your credentials have been updated.
        </div>
      )}
    </div>
  );
}
