import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { PieChart, Pie, Tooltip } from "recharts";
import SuperUserSignupForm from "./SuperUserSignupForm"; // Assuming the component name is SuperUserSignupForm
import "./superHome.css";

export default function SuperUserData() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const [showSignupForm, setShowSignupForm] = useState(false);
  const [data01, setData01] = useState([]);

  const navigate = useNavigate();

  const fetchData = () => {
    const superUserAuthenticated = localStorage.getItem(
      "superUserAuthenticated"
    );

    if (!superUserAuthenticated) {
      navigate("/superUser/login");
      return;
    }

    fetch("http://localhost:5000/getAllData")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        setLoading(false);
        setData01([
          { name: "Users", value: data.data.users.length },
          { name: "Companies", value: data.data.companies.length },
        ]);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  };

  useEffect(() => {
    fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const createSuperUser = () => {
    setShowSignupForm(true); // Show the signup form when creating a SuperUser
  };

  const deleteUser = (id, name) => {
    const confirmDelete = window.confirm(
      `Are you sure you want to delete ${name}`
    );
    if (confirmDelete) {
      fetch(`http://localhost:5000/deleteUser/${id}`, {
        method: "DELETE",
      })
        .then((response) => {
          if (response.status === 200) {
            fetchData();
          } else {
            console.error("Error deleting user:", response.statusText);
          }
        })
        .catch((error) => {
          console.error("Error deleting user:", error);
        });
    }
  };

  const deleteCompany = (id, name) => {
    const confirmDelete = window.confirm(
      `Are you sure you want to delete ${name}`
    );
    if (confirmDelete) {
      fetch(`http://localhost:5000/deleteCompany/${id}`, {
        method: "DELETE",
      })
        .then((response) => {
          if (response.status === 200) {
            fetchData();
          } else {
            console.error("Error deleting company:", response.statusText);
          }
        })
        .catch((error) => {
          console.error("Error deleting company:", error);
        });
    }
  };

  const toggleSignupForm = () => {
    setShowSignupForm(!showSignupForm);
  };

  const logout = () => {
    window.localStorage.clear();
    navigate("/superUser/login");
  };

  return (
    <div>
      <div className="button-container">
        <button className="btn btn-info" onClick={createSuperUser}>
          Create SuperUser
        </button>
      </div>

      {showSignupForm && (
        <SuperUserSignupForm
          toggleSignupForm={toggleSignupForm}
          fetchData={fetchData} // Pass the fetchData function to update the list
        />
      )}

      {/* ... Rest of your code ... */}
    </div>
  );
}
