import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import "./updateUser.css";

export default function UpdateUser() {
  const location = useLocation();
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [email, setEmail] = useState("");
  useEffect(() => {
    if (location.state?.data) {
      setFname(location.state.data.fname);
      setLname(location.state.data.lname);
      setEmail(location.state.data.email);
    }
  }, [location]);

  const updateData = () => {
    console.log(fname, lname);
  };
  return (
    <div>
      <form className="update_form">
        <h2 className="header">Update Account</h2>
        <div className="larger_inputContainer">
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="text"
              placeholder="First Name"
              name="fname"
              defaultValue={fname}
              onChange={(e) => setFname(e.target.value)}
              required
            />{" "}
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Last Name"
              name="fname"
              defaultValue={lname}
              onChange={(e) => setLname(e.target.value)}
              required
            />
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Email"
              name="fname"
              defaultValue={email}
              disabled
            />
            <button onClick={updateData} className="btn btn-primary button">
              Submit
            </button>
          </div>
        </div>
      </form>
    </div>
  );
}
