import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import "./signup.css";

export default function SignupComponent2({ visible, closeModal, register }) {
  const [formData, setFormData] = useState({
    fname: "",
    lname: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const [confirmPassword, setConfirmPassword] = useState("");
  const [isAccountCreated, setIsAccountCreated] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");
  const [userType, setUserType] = useState("");
  const [secretKey, setSecretKey] = useState("");
  const [formErrors, setFormErrors] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    setIsModalOpen(visible);
  }, [visible]);

  const validateForm = () => {
    const errors = {};

    if (!formData.fname) {
      errors.fname = "First Name is required";
    }

    if (!formData.lname) {
      errors.lname = "Last Name is required";
    }

    if (!formData.email) {
      errors.email = "Email is required";
    }

    if (!formData.password) {
      errors.password = "Password is required";
    }

    if (!formData.confirmPassword) {
      errors.confirmPassword = "Confirm Password is required";
    } else if (formData.password !== formData.confirmPassword) {
      errors.confirmPassword = "Passwords do not match";
    }

    setFormErrors(errors);

    return Object.keys(errors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });

    if (name === "confirmPassword") {
      setConfirmPassword(value);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (
      userType === "Company" &&
      secretKey !== "6eybj;l,;kp-=0-0-090979865e5322457t87{"
    ) {
      setFormErrors({
        ...formErrors,
        secretKey: "Invalid Company. Please enter the correct Secret Key.",
      });
      return;
    }

    if (formData.password !== confirmPassword) {
      setFormErrors({
        ...formErrors,
        confirmPassword: "Password and Confirm Password do not match.",
      });
      return;
    }

    if (validateForm()) {
      fetch("http://localhost:5000/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify({
          fname: formData.fname,
          lname: formData.lname,
          email: formData.email,
          password: formData.password,
          userType,
          secretKey: userType === "Company" ? secretKey : undefined,
        }),
      })
        .then((res) => {
          if (!res.ok) {
            throw new Error("Network response was not ok");
          }
          return res.json();
        })
        .then((data) => {
          setIsAccountCreated(true);
          setAlertMessage("Account Creation Successful");
        })
        .catch((error) => {
          setIsAccountCreated(false);
          console.error(error);

          if (
            error instanceof Error &&
            error.message.includes(
              "E11000 duplicate key error collection: dbcproject.User index: email_1"
            )
          ) {
            setFormErrors({
              ...formErrors,
              email: "Email is already in use. Please use a different email.",
            });
          } else {
            setFormErrors({
              ...formErrors,
              general: "Account Creation failure",
            });
          }
        });
    }
  };

  return (
    <Modal
      isOpen={isModalOpen}
      onRequestClose={closeModal}
      style={{
        overlay: {
          backgroundColor: "rgba(0, 0, 0, 0.5)",
        },
        content: {
          width: "600px",
          height: "500px",
          margin: "0px",
        },
      }}
    >
      <form onSubmit={handleSubmit}>
        <h1 className="heading">Signup</h1>
        {isAccountCreated && (
          <div className="alert alert-success" role="alert">
            {alertMessage}
          </div>
        )}
        <div className="larger_inputContainer">
          <div>
            Register AS
            <br />
            <label>
              <input
                type="radio"
                name="Usertype"
                value="user"
                checked={userType === "user"}
                onChange={(e) => setUserType(e.target.value)}
              />
              User
            </label>
            <br />
            <label>
              <input
                type="radio"
                name="Usertype"
                value="Company"
                checked={userType === "Company"}
                onChange={(e) => setUserType(e.target.value)}
              />
              Company
            </label>
          </div>

          {userType === "Company" && (
            <div className="mb-3">
              <input
                className="form-control larger_input"
                type="password"
                placeholder="Secret Key"
                onChange={(e) => setSecretKey(e.target.value)}
                required
              />
              {formErrors.secretKey && (
                <p className="error-message">{formErrors.secretKey}</p>
              )}
            </div>
          )}

          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="text"
              placeholder="First Name"
              name="fname"
              value={formData.fname}
              onChange={handleChange}
              required
            />
            {formErrors.fname && (
              <p className="error-message">{formErrors.fname}</p>
            )}
          </div>
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Last Name"
              name="lname"
              value={formData.lname}
              onChange={handleChange}
              required
            />
            {formErrors.lname && (
              <p className="error-message">{formErrors.lname}</p>
            )}
          </div>
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="email"
              placeholder="Email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            {formErrors.email && (
              <p className="error-message">{formErrors.email}</p>
            )}
          </div>
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="password"
              placeholder="Password"
              name="password"
              value={formData.password}
              onChange={handleChange}

            />
            {formErrors.password && (
              <p className="error-message">{formErrors.password}</p>
            )}
          </div>
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="password"
              placeholder="Confirm Password"
              name="confirmPassword"
              value={confirmPassword}
              onChange={handleChange}
              required
            />
            {formErrors.confirmPassword && (
              <p className="error-message">{formErrors.confirmPassword}</p>
            )}
          </div>
        </div>

        <button type="submit" className="btn btn-primary button">
          Submit
        </button>
      </form>
    </Modal>
  );
}
