import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom"; // Import Link for navigation

export default function CompanyHome({ companyData }) {
  const [userList, setUserList] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch user data or any other relevant data here
    const token = window.localStorage.getItem("token");

    fetch(`http://localhost:5000/C`, {
      method: "GET",
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "ok") {
          setUserList(data.data);
        }
        setLoading(false);
      });
  }, []);

  const logout = () => {
    window.localStorage.clear();
    navigate("/login"); // Redirect to the login page
  };

  // Check the role of the currently logged-in user (company)
  const isCompany = companyData.userType === "company";

  return (
    <div>
      <h5>Welcome {companyData.companyName}</h5>

      {isCompany && (
        // Render the update icon and link only for company users
        <Link to="/companyUpdate">
          <i className="fa-solid fa-user-pen"></i>
        </Link>
      )}

      <table style={{ width: 500 }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>User Type</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {loading ? (
            <tr>
              <td>Loading...</td>
            </tr>
          ) : (
            userList.map((user) => (
              <tr key={user._id}>
                <td>{user.fname}</td>
                <td>{user.email}</td>
                <td>{user.userType}</td>
                <td></td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      <button className="btn btn-primary" onClick={logout}>
        Logout
      </button>
    </div>
  );
}
