import React, { useState } from "react";
import Modal from "react-modal";

export default function CreateSuperUser({ visible, closeModal }) {
  const [formData, setFormData] = useState({
    fname: "",
    lname: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const [formErrors, setFormErrors] = useState({});

  const errorStyle = {
    color: "red", // Set the error message text color to red
    margin: "5px 0", // Add some margin for spacing
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Perform form validation
    const errors = {};
    if (!formData.fname) {
      errors.fname = "First Name is required";
    }
    if (!formData.lname) {
      errors.lname = "Last Name is required";
    }
    if (!formData.email) {
      errors.email = "Email is required";
    }
    if (!formData.password) {
      errors.password = "Password is required";
    }
    if (!formData.confirmPassword) {
      errors.confirmPassword = "Confirm Password is required";
    }
    if (formData.password !== formData.confirmPassword) {
      errors.confirmPassword = "Passwords do not match";
    }

    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    // Reset form errors
    setFormErrors({});

    // Continue with the submission
    // ... (API call to create the super user)
  };

  return (
    <Modal
      isOpen={visible}
      onRequestClose={closeModal}
      style={{
        overlay: {
          backgroundColor: "rgba(0, 0, 0, 0.5)", // Semi-transparent background
        },
        content: {
          width: "600px",
          height: "600px",
        },
      }}
    >
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            name="fname"
            value={formData.fname}
            placeholder="First Name"
            onChange={handleChange}
          />
          {formErrors.fname && (
            <p style={errorStyle}>{formErrors.fname}</p>
          )}
        </div>
        <div>
          <input
            type="text"
            name="lname"
            value={formData.lname}
            placeholder="Last Name"
            onChange={handleChange}
          />
          {formErrors.lname && (
            <p style={errorStyle}>{formErrors.lname}</p>
          )}
        </div>
        <div>
          <input
            type="email"
            name="email"
            value={formData.email}
            placeholder="Email"
            onChange={handleChange}
          />
          {formErrors.email && (
            <p style={errorStyle}>{formErrors.email}</p>
          )}
        </div>
        <div>
          <input
            type="password"
            name="password"
            value={formData.password}
            placeholder="Password"
            onChange={handleChange}
          />
          {formErrors.password && (
            <p style={errorStyle}>{formErrors.password}</p>
          )}
        </div>
        <div>
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            placeholder="Confirm password"
            onChange={handleChange}
          />
          {formErrors.confirmPassword && (
            <p style={errorStyle}>{formErrors.confirmPassword}</p>
          )}
        </div>
        <br />
        <button className="btn btn-success" type="submit">
          Create Super User
        </button>
      </form>
    </Modal>
  );
}
