import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import "./signup.css";

const SignupComponent = ({ visible, closeModal, register }) => {
  const [formData, setFormData] = useState({
    fname: "",
    lname: "",
    email: "",
    password: "",
    confirmPassword: "",
    userType: "user",
    companyName: "",
  });

  const [confirmPassword, setConfirmPassword] = useState("");
  const [isAccountCreated, setIsAccountCreated] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");
  const [userType, setUserType] = useState("");
  const [secretKey, setSecretKey] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    setIsModalOpen(visible);
  }, [visible]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    if (name === "confirmPassword") {
      setConfirmPassword(value);
    }
  };

  const handleCloseAlert = () => {
    setAlertMessage("");
  };

  const handleCloseSuccess = () => {
    setSuccessMessage("");
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (userType === "Company" && secretKey !== "yourSecretKey") {
      setAlertMessage("Invalid Company. Please enter the correct Secret Key.");
      setIsAccountCreated(false);
      return;
    }

    if (formData.password !== confirmPassword) {
      setAlertMessage("Password and Confirm Password do not match.");
      setIsAccountCreated(false);
      return;
    }

    fetch("http://localhost:5000/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        fname: formData.fname,
        lname: formData.lname,
        email: formData.email,
        password: formData.password,
        userType,
        secretKey: userType === "Company" ? secretKey : undefined,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        setSuccessMessage("Account created successfully");
        setIsAccountCreated(true);
      })
      .catch((error) => {
        setIsAccountCreated(false);
        console.error(error);

        if (
          error instanceof Error &&
          error.message.includes(
            "E11000 duplicate key error collection: dbcproject.User index: email_1"
          )
        ) {
          setErrorMessage(
            "Email is already in use. Please use a different email."
          );
        } else {
          setErrorMessage("Account Creation failure");
        }
      });
  };

  return (
    <Modal
      isOpen={isModalOpen}
      onRequestClose={closeModal}
      style={{
        overlay: {
          backgroundColor: "rgba(0, 0, 0, 0.5)",
        },
        content: {
          width: "600px",
          height: "500px",
          margin: "0px",
        },
      }}
    >
      <div>
        <form onSubmit={handleSubmit}>
          {isAccountCreated && (
            <p
              className="success-message alert alert-success"
              style={{ color: "green" }}
              onClick={handleCloseSuccess}
            >
              {successMessage} <span aria-hidden="true">X</span>
            </p>
          )}
          <br />

          {alertMessage && (
            <div className="alert alert-danger" role="alert">
              {alertMessage}{" "}
              <span
                className="close"
                aria-label="Close"
                onClick={handleCloseAlert}
              >
                X
              </span>
            </div>
          )}
          <h1 className="heading">Signup</h1>
          <div className="larger_inputContainer">
            {/* ... (rest of your form components) */}
          </div>

          <button type="submit" className="btn btn-primary button">
            Submit
          </button>
        </form>
      </div>
    </Modal>
  );
};

export default SignupComponent;
