import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import "./signup.css";


export default function SignupComponent2({ visible, closeModal, register }) {
  const [formData, setFormData] = useState({
    fname: "",
    lname: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const [confirmPassword, setConfirmPassword] = useState("");
  const [isAccountCreated, setIsAccountCreated] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");
  const [userType, setUserType] = useState("");
  const [secretKey, setSecretKey] = useState("");

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });


  useEffect(() => {
    setIsModalOpen(visible);
  }, [visible]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  if (name === "confirmPassword") {
    setConfirmPassword(value);
  }
};

const handleSubmit = (e) => {
  e.preventDefault();

  if (
    userType === "Company" &&
    secretKey !== "6eybj;l,;kp-=0-0-090979865e5322457t87{"
  ) {
    alert("Invalid Company. Please enter the correct Secret Key.");
    return;
  }

  if (formData.password !== confirmPassword) {
    alert("Password and Confirm Password do not match.");
    return;
  }

  fetch("http://localhost:5000/register", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
    },
    body: JSON.stringify({
      fname: formData.fname,
      lname: formData.lname,
      email: formData.email,
      password: formData.password,
      userType,
      secretKey: userType === "Company" ? secretKey : undefined,
    }),
  })
    .then((res) => {
      if (!res.ok) {
        throw new Error("Network response was not ok");
      }
      return res.json();
    })
    .then((data) => {
      setIsAccountCreated(true);
      setAlertMessage("Account Creation Successful");
    })
    .catch((error) => {
      setIsAccountCreated(false);
      console.error(error); // Log the error to the console for debugging

      if (error instanceof Error && error.message.includes('E11000 duplicate key error collection: dbcproject.User index: email_1')) {
        setAlertMessage("Email is already in use. Please use a different email.");
      } else {
        setAlertMessage("Account Creation failure");
      }
    });

};

  return (
    <Modal
      isOpen={isModalOpen}
      onRequestClose={closeModal}
      style={{
        overlay: {
          backgroundColor: "rgba(0, 0, 0, 0.5)",
        },
        content: {
          width: "600px",
          height: "500px",
          margin: '0px'
        },
      }}
    >
      <form className="signup_form" onSubmit={handleSubmit}>
        <h1 className="heading">Signup</h1>
        {isAccountCreated && (
          <div className="alert alert-success" role="alert">
            {alertMessage}
          </div>
        )}
        <div className="larger_inputContainer">
          <div>
            Register AS
            <br />
            <input
              type="radio"
              name="Usertype"
              value="user"
              onChange={(e) => setUserType(e.target.value)}
            />
            User
            <br />
            <input
              type="radio"
              name="Usertype"
              value="Company"
              onChange={(e) => setUserType(e.target.value)}
            />
            Company
          </div>

          {formData.userType === "company" ? (
            <div className="mb-3">
              <input
                className="form-control larger_input"
                type="text"
                placeholder="Company Name"
                name="companyName"
                value={formData.companyName}
                onChange={handleChange}
                required
              />
              {formErrors.companyName && (
                <p className="error-message">{formErrors.companyName}</p>
              )}
            </div>
          ) : null}

          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="text"
              placeholder="First Name"
              name="fname"
              value={formData.fname}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Last Name"
              name="lname"
              value={formData.lname}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="email"
              placeholder="Email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            {formErrors.email && (
              <p className="error-message">{formErrors.email}</p>
            )}
          </div>
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="password"
              placeholder="Password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            {formErrors.password && (
              <p className="error-message">{formErrors.password}</p>
            )}
          </div>
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="password"
              placeholder="Confirm Password"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
            />
            {formErrors.confirmPassword && (
              <p className="error-message">{formErrors.confirmPassword}</p>
            )}
          </div>
        </div>

        <br />
        <button className="btn btn-success" type="submit">
          Sign Up
        </button>
      </form>
    </Modal>
  );
}
