import React, { useState } from "react";
import { Link, usenavigate, useNavigate } from "react-router-dom";
import "./login.css";

export default function LoginComponent() {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // Use the usenavigate hook to programmatically navigate to other pages
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await fetch("http://localhost:5000/login-user", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: formData.email,
          password: formData.password,
          userType: "user", // You can change this to the selected userType
        }),
      });

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      const data = await response.json();

      if (data.status === "ok") {
        // Store the JWT token in local storage for subsequent requests
        window.localStorage.setItem("token", data.data);
        window.localStorage.setItem("loggedIn", true);

        // Use navigate.push to navigate to the user's dashboard or another authenticated page
        navigate.push("/userDetails");
      } else if (data.error === "Invalid Password") {
        setError("Invalid Password");
      } else {
        setError("Login Unsuccessful");
      }
    } catch (error) {
      setError("Network error. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <div className="whole_page">
      <div className="random">
        <h4>Welcome To this Job portal page</h4>
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Inventore
          culpa corporis fugiat iste praesentium esse doloremque quas cumque
        </p>
      </div>
      <form className="login_form" onSubmit={handleSubmit}>
        <h1 className="heading">Login</h1>
        <div className="large_inputContainer">
          <div className="mb-3">
            <input
              type="email"
              className="form-control large_input"
              placeholder="Email"
              id="exampleInputEmail1"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="mb-3">
            <input
              type="password"
              className="form-control large_input"
              placeholder="Password"
              id="exampleInputPassword1"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              required
            />
          </div>
        </div>

        {error && <div className="error-message">{error}</div>}

        <button
          type="submit"
          className="btn btn-primary large_button"
          disabled={loading}
        >
          {loading ? "Logging in..." : "Login"}
        </button>
        <br />
        <div className="no_account">
          <Link to="/register">Don't have an account? Signup</Link>
        </div>
      </form>
    </div>
  );
}
