import React, { useState, useEffect } from "react"; // Import useState and useEffect

export default function SuperUserData() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const [passcode, setPasscode] = useState(""); // Store the passcode entered by the user
  const [isAuthorized, setIsAuthorized] = useState(false);

  // Define your superuser passcode (you may want to store this securely)
  const superUserPasscode = "1234567";

  useEffect(() => {
    // Check if the user is authorized based on the passcode
    if (superUserToken && passcode === superUserPasscode) {
      setIsAuthorized(true);
      fetchData();
    } else {
      setIsAuthorized(false);
    }
  }, [superUserToken, passcode]);

  const fetchData = () => {
    // Fetch data from the server using superUserToken
    fetch("http://localhost:5000/getAllData", {
      method: "GET",
      headers: {
        "x-superuser-token": superUserToken,
      },
    })
      .then((response) => {
        if (response.status === 401) {
          // Handle unauthorized access here, such as redirecting to the login page.
        } else {
          return response.json();
        }
      })
      .then((data) => {
        setData(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  const createCompany = () => {
    // Implement the logic for creating a new company here
    // You can show a form or send a request to the server
  };

  const deleteUser = (id, name) => {
    const confirmDelete = window.confirm(
      `Are you sure you want to delete ${name}`
    );
    if (confirmDelete) {
      fetch(`http://localhost:5000/deleteUser/${id}`, {
        method: "DELETE",
      })
        .then((response) => {
          if (response.status === 200) {
            // User was deleted successfully, refresh the data
            fetchData();
          } else {
            console.error("Error deleting user:", response.statusText);
          }
        })
        .catch((error) => {
          console.error("Error deleting user:", error);
        });
    }
  };

  const deleteCompany = (id, name) => {
    const confirmDelete = window.confirm(
      `Are you sure you want to delete ${name}`
    );
    if (confirmDelete) {
      fetch(`http://localhost:5000/deleteCompany/${id}`, {
        method: "DELETE",
      })
        .then((response) => {
          if (response.status === 200) {
            // Company was deleted successfully, refresh the data
            fetchData();
          } else {
            console.error("Error deleting company:", response.statusText);
          }
        })
        .catch((error) => {
          console.error("Error deleting company:", error);
        });
    }
  };

  const logout = () => {
    localStorage.clear();
    window.location.href = "/superUser/login";
  };

  if (!isAuthorized) {
    // Redirect or display an access-denied message
    return (
      <div>
        <h1>Access Denied</h1>
      </div>
    );
  }

  return (
    <div>
      <div className="button-container">
        <button onClick={createCompany} className="btn btn-success">
          Create Company
        </button>
      </div>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <table className="table table-striped table-bordered">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Email</th>
                <th>User Type</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {data.data.users.map((user) => (
                <tr key={user._id}>
                  <td>{user._id}</td>
                  <td>
                    {user.fname} {user.lname}
                  </td>
                  <td>{user.email}</td>
                  <td>{user.userType}</td>
                  <td
                    className="btn btn-danger"
                    onClick={() =>
                      deleteUser(user._id, `${user.fname} ${user.lname}`)
                    }
                  >
                    Delete
                  </td>
                </tr>
              ))}
              {data.data.companies.map((company) => (
                <tr key={company._id}>
                  <td>{company._id}</td>
                  <td>
                    {company.fname} {company.lname}
                  </td>
                  <td>{company.email}</td>
                  <td>{company.userType}</td>
                  <td
                    className="btn btn-danger"
                    onClick={() =>
                      deleteCompany(company._id, `${company.email}`)
                    }
                  >
                    Delete
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <p>TOTAL COMPANIES:{data.data.companies.length}</p>
          <p>TOTAL USERS:{data.data.users.length}</p>
          <button className="btn btn-primary" onClick={logout}>
            Logout
          </button>
        </div>
      )}
    </div>
  );
}
