import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import UpdateCompany from "./updateCompany";

export default function CompanyHome() {
  const navigate = useNavigate();
  const [modalVisible, setModalVisible] = useState(false);
  const [companyData, setCompanyData] = useState({
    companyData: "",
  });

  const logout = () => {
    window.localStorage.clear();
    window.location.href = "/login";
  };

  useEffect(() => {
    const token = window.localStorage.getItem("token");
    if (!token) {
      navigate("/login");

      return;
    }
    fetch("http://localhost:5000/companyData", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        token,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        if (data.status === "ok") {
          setCompanyData({
            companyData: data.data,
          });
        }
      });
  }, [navigate]);
  const openModal = () => {
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  return (
    <div>
      <div>
        {/* <i
          className="fa-solid fa-user-pen"
          onClick={() => navigate("/updateCompany", { state: companyData })}
        ></i> */}
         <i className="fa-solid fa-user-pen" onClick={openModal}>
          </i>
          <UpdateCompany visible={modalVisible} closeModal={closeModal} />
        {companyData.companyData.email}
        <p>Name</p>
        <h4>{companyData.companyData.fname}</h4>
      </div>
      <div>
        <p>Email</p>
        <h4>{companyData.companyData.email}</h4>
      </div>
      <br />
      <button className="btn btn-primary" onClick={logout}>
        Logout
      </button>
    </div>
  );
}
