import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import "./updateUser.css";

export default function UpdateUser() {
  const location = useLocation();
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [email, setEmail] = useState("");

  useEffect(() => {
    if (location.state) {
      setFname(location.state.data.fname);
      setLname(location.state.data.lname);
      setEmail(location.state.data.email);
    }
  }, [location]);

  const updateData = () => {
    fetch("http://localhost:5000/updateUser", {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        id: location.state.data._id,
        fname: location.state.data.fname,
        lname: location.state.data.lname,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        // console.log(data);
        if (location.status === "ok") {
          window.location.href = "/userDetails";
        }
        // console.log(location)
      });
  };

  return (
    <div>
      <form className="update_form">
        <h2 className="header">Update Account</h2>
        <div className="larger_inputContainer">
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="text"
              placeholder="First Name"
              name="fname"
              value={fname}
              onChange={(e) => setFname(e.target.value)}
              required
            />
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Last Name"
              name="lname"
              value={lname}
              onChange={(e) => setLname(e.target.value)}
              required
            />
            <input
              className="form-control larger_input"
              type="text"
              placeholder="Email"
              name="email"
              value={email}
              disabled
            />
            <button onClick={updateData} className="btn btn-primary button">
              Submit
            </button>
          </div>
        </div>
      </form>
    </div>
  );
}
