import React, { useState, useEffect } from "react";

export default function CompanyHome({us}) {
  const [userData, setUserData] = useState([]);
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    const token = window.localStorage.getItem("token");

    fetch(`http://localhost:5000/getAllUser?token=${token}`, {
      method: "GET",
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "ok") {
          setUserData(data.data); // Update the state with user data
        }
        setLoading(false); // Set loading to false after data is fetched
      });
  }, []);

  const logout = () => {
    window.localStorage.clear();
    window.location.href = '/login';
  }

  return (
    <div>
      <h5>Welcome Company</h5>
      <table style={{ width: 500 }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>User Type</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {loading ? (
            <tr>
              <td>Loading...</td>
            </tr>
          ) : (
            userData.map((user) => (
              <tr key={user._id}>
                <td>{user.fname}</td>
                <td>{user.email}</td>
                <td>{user.userType}</td>
                <td></td>
              </tr>
            ))
          )}
        </tbody>
      </table>
      <button className="btn btn-primary" onClick={logout}>
        Logout
      </button>
    </div>
  );
}
