import React, { useState } from "react";
import Modal from "react-modal";

export default function CreateSuperUser({ visible, closeModal }) {
  const [formData, setFormData] = useState({
    fname: "",
    lname: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const [formErrors, setFormErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Perform form validation
    const errors = {};
    if (!formData.fname) {
      errors.fname = "First Name is required";
    }
    if (!formData.lname) {
      errors.lname = "Last Name is required";
    }
    if (!formData.email) {
      errors.email = "Email is required";
    }
    if (!formData.password) {
      errors.password = "Password is required";
    }
    if (!formData.confirmPassword) {
      errors.confirmPassword = "Confirm Password is required";
    }
    if (formData.password !== formData.confirmPassword) {
      errors.confirmPassword = "Passwords do not match";
    }

    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    // Reset form errors
    setFormErrors({});

    // Continue with the submission
    const dataToSend = {
      fname: formData.fname,
      lname: formData.lname,
      email: formData.email,
      password: formData.password,
    };

    // Make an API call to create the super user
    fetch("http://localhost:5000/createSuperUser", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(dataToSend),
    })
      .then((response) => response.json())
      .then((data) => {
        // Handle the API response, e.g., show a success message or redirect to another page
        console.log(data);
      })
      .catch((error) => {
        // Handle any errors, e.g., display an error message
        console.error(error);
      });
  };

  return (
    <Modal
      isOpen={visible}
      onRequestClose={closeModal}
      style={{
        overlay: {
          backgroundColor: "rgba(0, 0, 0, 0.5)", // Semi-transparent background
        },
        content: {
          width: "600px",
          height: "600px",
        },
      }}
    >
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            name="fname"
            value={formData.fname}
            placeholder="First Name"
            onChange={handleChange}
          />
          {formErrors.fname && <p className="error">{formErrors.fname}</p>}
        </div>
        <div>
          <input
            type="text"
            name="lname"
            value={formData.lname}
            placeholder="Last Name"
            onChange={handleChange}
          />
          {formErrors.lname && <p className="error">{formErrors.lname}</p>}
        </div>
        <div>
          <input
            type="email"
            name="email"
            value={formData.email}
            placeholder="Email"
            onChange={handleChange}
          />
          {formErrors.email && <p className="error">{formErrors.email}</p>}
        </div>
        <div>
          <input
            type="password"
            name="password"
            value={formData.password}
            placeholder="Password"
            onChange={handleChange}
          />
          {formErrors.password && <p className="error">{formErrors.password}</p>}
        </div>
        <div>
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            placeholder="Confirm password"
            onChange={handleChange}
          />
          {formErrors.confirmPassword && (
            <p className="error">{formErrors.confirmPassword}</p>
          )}
        </div>
        <br />
        <button className="btn btn-success" type="submit">
          Create Super User
        </button>
      </form>
    </Modal>
  );
}
