import React, { useState } from "react";
import { Link } from "react-router-dom";

export default function SuperUserSignUpComponent() {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    confirmPassword: "",
  });

  const [isSignUpSuccessful, setIsSignUpSuccessful] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      alert("Password and Confirm Password do not match.");
      return;
    }

    fetch("http://localhost:5000/superUserSignUp", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        email: formData.email,
        password: formData.password,
      }),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error("Network response was not ok");
        }
        return res.json();
      })
      .then((data) => {
        setIsSignUpSuccessful(true);
        setAlertMessage("Sign-Up Successful");
      })
      .catch((error) => {
        setIsSignUpSuccessful(false);
        setAlertMessage("Sign-Up failed. Check your credentials.");
      });
  };

  return (
    <div>
      <form className="signup_form" onSubmit={handleSubmit}>
        <h1 className="heading">Super User Sign-Up</h1>
        {isSignUpSuccessful && (
          <div className="alert alert-success" role="alert">
            {alertMessage}
          </div>
        )}
        <div className="larger_inputContainer">
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="email"
              placeholder="Email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="password"
              placeholder="Password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="mb-3">
            <input
              className="form-control larger_input"
              type="password"
              placeholder="Confirm Password"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleInputChange}
              required
            />
          </div>
        </div>
        <button type="submit" className="btn btn-primary button">
          Sign Up
        </button>
        <div className="own_account">
          <Link to="/superUser/signin">Already have an account? Sign In</Link>
        </div>
      </form>
    </div>
  );
}
