import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import "./signup.css";

export default function SignupComponent2({ visible, closeModal }) {
  const [formData, setFormData] = useState({
    fname: "",
    lname: "",
    email: "",
    password: "",
    confirmPassword: "",
    userType: "user", // Default to "user"
  });

  const [formErrors, setFormErrors] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    setIsModalOpen(visible);
  }, [visible]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.email) {
      errors.email = "Email is required";
    }

    if (!formData.password) {
      errors.password = "Password is required";
    }

    if (!formData.confirmPassword) {
      errors.confirmPassword = "Confirm Password is required";
    } else if (formData.password !== formData.confirmPassword) {
      errors.confirmPassword = "Passwords do not match";
    }

    setFormErrors(errors);

    return Object.keys(errors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      // Add your sign-up logic here
      // For demonstration purposes, I'm using a setTimeout to simulate a successful sign-up
      setTimeout(() => {
        setSuccessMessage("Sign-up successful");
        setErrorMessage("");
        setFormData({
          fname: "",
          lname: "",
          email: "",
          password: "",
          confirmPassword: "",
          userType: "user", // Reset to "user" after successful signup
        });

        setTimeout(() => {
          setSuccessMessage("");
          closeModal();
        }, 2000);
      }, 1000);
    }
  };

  return (
    <Modal
      isOpen={isModalOpen}
      onRequestClose={closeModal}
      style={{
        overlay: {
          backgroundColor: "rgba(0, 0, 0, 0.5)",
        },
        content: {
          width: "400px",
          height: "400px",
          margin: "auto",
        },
      }}
    >
      <form onSubmit={handleSubmit}>
        {successMessage && (
          <p className="success-message" style={{ color: "green" }}>
            {successMessage}
          </p>
        )}
        {errorMessage && (
          <p className="error-message" style={{ color: "red" }}>
            {errorMessage}
          </p>
        )}
        <div>
        <div className="larger_inputContainer">
          <div>
            Register AS
            <br />
            <input
              type="radio"
              name="Usertype"
              value="user"
              onChange={(e) => setUserType(e.target.value)}
            />
            User
            <br />
            <input
              type="radio"
              name="Usertype"
              value="Company"
              onChange={(e) => setUserType(e.target.value)}
            />
            Company
          </div>

          {userType === "Company" ? (
            <div className="mb-3">
              <input
                className="form-control larger_input"
                type="password"
                placeholder="Secret Key"
                onChange={(e) => setSecretKey(e.target.value)}
                required
              />
            </div>
          ) : null}
          <input
            type="text"
            name="fname"
            value={formData.fname}
            placeholder="First Name"
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <input
            type="text"
            name="lname"
            value={formData.lname}
            placeholder="Last Name"
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <input
            type="email"
            name="email"
            value={formData.email}
            placeholder="Email"
            onChange={handleChange}
            required
          />
          {formErrors.email && (
            <p className="error-message">{formErrors.email}</p>
          )}
        </div>
        <div>
          <input
            type="password"
            name="password"
            value={formData.password}
            placeholder="Password"
            onChange={handleChange}
            required
          />
          {formErrors.password && (
            <p className="error-message">{formErrors.password}</p>
          )}
        </div>
        <div>
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            placeholder="Confirm Password"
            onChange={handleChange}
            required
          />
          {formErrors.confirmPassword && (
            <p className="error-message">{formErrors.confirmPassword}</p>
          )}
        </div>
        </div>
        <br />
        <button className="btn btn-success" type="submit">
          Sign Up
        </button>
      </form>
    </Modal>
  );
}
