import React, { useState, useEffect } from "react";
import Modal from "react-modal";

export default function UpdateCompany(visible, closeModal) {
  const [isModalOpen, setIsModalOpen] = useState(false);
  useEffect(() => {
    setIsModalOpen(visible);
  }, [visible]);
  useEffect(() => {
    if (location.state?.companyData) {
      const { fname, lname } = location.state.companyData;
      setFname(fname || "");
      setLname(lname || "");
    }
  }, [location]);

  const updateData = () => {
    fetch("http://localhost:5000/updateCompany", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        id: location.state.companyData._id,
        fname,
        lname,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "ok") {
          setUpdateSuccess(true);
          navigate("/companyHome");
        }
      });
  };
  return (
    <Modal
      isOpen={isModalOpen}
      onRequestClose={closeModal}
      style={{
        overlay: {
          backgroundColor: "rgba(0, 0, 0, 0.5)",
        },
        content: {
          width: "600px",
          height: "600px",
          transition: "right 0.3s ease",
          right: isModalOpen ? 0 : "-600px",
        },
      }}
    >
      <div className="update_form">
        <h2 className="header">Update Company Data</h2>
        <div className="larger_inputContainer">
          <input
            className="larger_input"
            type="text"
            placeholder="First Name"
            name="fname"
            value={fname}
            onChange={(e) => setFname(e.target.value)}
            required
          />
          <input
            className="larger_input"
            type="text"
            placeholder="Last Name"
            name="lname"
            value={lname}
            onChange={(e) => setLname(e.target.value)}
            required
          />
          <input
            className="larger_input"
            type="text"
            placeholder="Email"
            name="email"
            value={location.state.companyData.email}
            disabled
          />
          <button onClick={updateData} className="btn btn-success">
            Submit
          </button>
        </div>
        {updateSuccess && (
          <div className="alert alert-success" role="alert">
            Update successful! Company data has been updated.
          </div>
        )}
      </div>
    </Modal>
  );
}
