import React, { useState, useEffect } from "react";
import CompanyHome from "./CompanyHome"; // Update the import path

export default function CompanyDashboard() {
  const [companyData, setCompanyData] = useState(null); // State for company data
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState("");

  useEffect(() => {
    const token = window.localStorage.getItem("token");

    // Fetch company data based on the user's token
    fetch("http://localhost:5000/companyData", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        token,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "ok") {
          setCompanyData(data.data);
        } else {
          setError("Failed to fetch company data");
        }
        setLoading(false);
      })
      .catch((error) => {
        setError("Network error. Please try again later.");
        setLoading(false);
      });
  }, []);

  const logout = () => {
    window.localStorage.clear();
    window.location.href = "/login";
  };

  return (
    <div>
      <p>Welcome {companyData ? companyData.email : "Loading..."} Company</p>
      <CompanyHome companyData={companyData} /> {/* Pass companyData as a prop */}
      <button className="btn btn-primary" onClick={logout}>
        Logout
      </button>
      {error && <div className="error-message">{error}</div>}
    </div>
  );
}
