import React, { useEffect, useState } from "react";
import "./userDetails.css";
import UserHome from "../userHome";
import CompanyHome from "../companyHome";

export default function UserDetails() {
  const [userData, setUserData] = useState(null);
  const [isCompany, setIsCompany] = useState(false);

  useEffect(() => {
    const token = window.localStorage.getItem("token");

    if (token) {
      fetch("http://localhost:5000/userData", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          token: token,
        }),
      })
        .then((res) => {
          if (!res.ok) {
            throw new Error("Network response was not ok");
          }
          return res.json();
        })
        .then((data) => {
          if (data.status === "ok") {
            setUserData(data.data);
            if (data.data && data.data.userType === "Company") {
              setIsCompany(true);
            }
          } else if (data.status === "error" && data.data === "token expired") {
            alert("Token expired. Please log in again.");
            window.localStorage.clear();
            window.location.href = "/login";
          }
        })
        .catch((error) => {
          console.error("Error:", error);
        });
    } else {
      // Handle the case where there is no token in local storage (user is not logged in)
      window.location.href = "/login";
    }
  }, []);

  const logOut = () => {
    window.localStorage.clear();
    window.location.href = "/login";
    alert(`See you soon ${userData.data.fname}`);
  };

  return (
    <div className="details">
      {isCompany && <CompanyHome userData={userData} />}
      {userData && userData.data && !isCompany && (
        <>
          <UserHome userData={userData.data} />
          <button onClick={logOut} className="logout_button btn btn-primary">
            Logout
          </button>
        </>
      )}
    </div>
  );
}
