import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./updateCompany.css"; // Create a separate CSS file for styling if needed.

export default function UpdateCompany() {
  const location = useLocation();
  const navigate = useNavigate();
  const [companyName, setCompanyName] = useState("");
  const [updateSuccess, setUpdateSuccess] = useState(false);

  useEffect(() => {
    if (location.state?.companyData) {
      const { companyName } = location.state.companyData;
      setCompanyName(companyName || "");
    }
  }, [location]);

  const updateData = () => {
    fetch("http://localhost:5000/updateCompany", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({
        id: location.state.companyData._id,
        companyName,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.status) {
          setUpdateSuccess(true);
          navigate("/companyHome");
        }
      });
  };

  return (
    <div className="update_form">
      <h2 className="header">Update Company Information</h2>
      <div className="larger_inputContainer">
        <input
          className="larger_input"
          type="text"
          placeholder="Company Name"
          name="companyName"
          value={companyName}
          onChange={(e) => setCompanyName(e.target.value)}
          required
        />
        {/* You may add more input fields for other company information here. */}
        <button onClick={updateData} className="btn btn-success">
          Submit
        </button>
      </div>
      {updateSuccess && (
        <div className="alert alert-success" role="alert">
          Update successful! Company information has been updated.
        </div>
      )}
    </div>
  );
}
