import React, { useState } from "react";
import Modal from "react-modal";

export default function CreateSuperUser({ visible, closeModal }) {
  const [formData, setFormData] = useState({
    fname: "",
    lname: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Create a new SuperUser instance with the form data
      const superUser = new SuperUser({
        fname: formData.fname,
        lname: formData.lname,
        email: formData.email,
        password: formData.password,
        userType: "superuser",
      });

      // Save the SuperUser instance to the database
      await superUser.save();

      // Handle success (e.g., show a success message)
      console.log("SuperUser created successfully");

      // Optionally, clear the form fields or close the modal
      setFormData({
        fname: "",
        lname: "",
        email: "",
        password: "",
        confirmPassword: "",
      });
    } catch (error) {
      // Handle errors (e.g., show an error message)
      console.error("Error creating SuperUser:", error);
    }
  }


  return (
    <Modal
      isOpen={visible}
      onRequestClose={closeModal}
      style={{
        overlay: {
          backgroundColor: "rgba(0, 0, 0, 0.5)", // Semi-transparent background
        },
        content: {
          width: "600px",
          height: "600px",
        },
      }}
    >
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            name="fname"
            value={formData.fname}
            placeholder="First Name"
            onChange={handleChange}
          />
        </div>
        <div>
          <input
            type="text"
            name="lname"
            value={formData.lname}
            placeholder="Last Name"
            onChange={handleChange}
          />
        </div>
        <div>
          <input
            type="email"
            name="email"
            value={formData.email}
            placeholder="Email"
            onChange={handleChange}
          />
        </div>
        <div>
          <input
            type="password"
            name="password"
            value={formData.password}
            placeholder="Password"
            onChange={handleChange}
          />
        </div>
        <div>
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            placeholder="Confirm password"
            onChange={handleChange}
          />
        </div>
        <br />
        <button className="btn btn-success" type="submit">
          Create Super User
        </button>
      </form>
    </Modal>
  );
}
